        sqlC = self.databaseConn()
        sqlC.execute("SELECT DISTINCT Tname FROM Teams")
        teamNames = sqlC.fetchall()
        for i in teamNames:
            self.CB1.addItem(i[0])

        sqlC.execute("SELECT DISTINCT Matches FROM Match")
