    def teamList(self):
        selectedTeam = self.CB1.currentText()
        sqlC = self.databaseConn()
        sqlC.execute("SELECT Players FROM Teams WHERE Tname = ?",(selectedTeam,))
        players = sqlC.fetchall()
        self.Player_listWidget.clear()
        for i in players:
            self.playersList.append(i[0])
            self.Player_listWidget.addItem(i[0])

    def cal(self):
        self.points = 0
        self.total = 0
        self.players = []
        self.pointsList = []
        self.Points_listWidget.clear()

        self.lineEdit.setText(str(self.total))
        team = self.CB1.currentText()
        match = self.CB2.currentText()
        sqlC = self.databaseConn()
        for i in range(self.Player_listWidget.count()):
            self.players.append(self.Player_listWidget.item(i).text())
        #print(players)
        for player in self.players:
            sqlC.execute("SELECT * FROM Match WHERE Players = ?",(player,))
            score = sqlC.fetchall()
            #print(score)
            if score[0][12] == match:
                self.points = FantasyCricket.FantasyCricket.BattingBowlingPoints.batting(score)
                
            self.Points_listWidget.addItem(str(self.points))
            self.pointsList.append(self.points)
            #print("points",self.pointsList)
            
        for i in self.pointsList:
            self.total += i
        self.lineEdit.setText(str(self.total))
        
            
    def points(self):
        if "----SELECT TEAM----" == self.CB1.currentText() and "----SELECT MATCH----" == self.CB2.currentText():
            self.msgbox("Warning","Select a Team and Match to evaluate","Images of Fantasy Cricket\\TM.png")
        elif "----SELECT TEAM----" == self.CB1.currentText() and "----SELECT MATCH----" != self.CB2.currentText():
            self.msgbox("Warning","Select a Team to evaluate","Images of Fantasy Cricket\\team4.jpg")
        elif "----SELECT TEAM----" != self.CB1.currentText() and "----SELECT MATCH----" == self.CB2.currentText():
            self.msgbox("Warning","Select a Match to evaluate","Images of Fantasy Cricket\\match.jpg")   
        else:
            self.cal()
            
                    
